import React, { useState } from 'react';
import './Navbar.css';  // Ajoutez votre propre style ici pour la personnalisation du CSS
import logo from './assets/logo.webpb'; // Ajuste le chemin en fonction de l'emplacement de ton image

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <a href="/">
          <img src={logo} alt="Logo Dark Vapor" />
        </a>
      </div>
      <div className={`hamburger ${isOpen ? 'open' : ''}`} onClick={toggleMenu}>
        <span className="bar"></span>
        <span className="bar"></span>
        <span className="bar"></span>
      </div>
      <ul className={`nav-links ${isOpen ? 'show-menu' : ''}`}>
        <li><a href="/">Accueil</a></li>
        <li><a href="/clients">Clients</a></li>
        <li><a href="/stock">Stock</a></li>
        <li><a href="/commandes">Commandes</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
